package com.hunter.common.constant;/** * @Author zhangcl * @Date 17/5/22  下午11:25 */public enum RoomTypeEnum {    NORMAL(1, "普通房间"), VIP(2, "vip房间"), MATCH(3, "匹配房间");    private int code;    private String name;    RoomTypeEnum(int code, String name) {        this.code = code;        this.name = name;    }    public int getCode() {        return code;    }    public String getName() {        return name;    }    /**     * 根据name获取id     *     * @param name     * @return     */    public int getIndexByName(String name) {        for (RoomTypeEnum userTypeEnum : RoomTypeEnum.values()) {            if (userTypeEnum.getName().equals(name)) {                return userTypeEnum.getCode();            }        }        return -1;    }}