package com.hunter.sys.user.mapper;import com.hunter.sys.user.entity.SysUser;import org.apache.ibatis.annotations.ResultMap;import org.apache.ibatis.annotations.Select;import org.springframework.stereotype.Repository;/** * @Author zhangcl * @Date 17/5/24  下午9:30 */@Repositorypublic interface SysUserMapper {    /**     * 根据map查找用户     * @param type     * @param Phone     * @return     */    @Select("select a.* from p_user a where a.has_del=0 " +            "and a.user_type=#{0} and a.phone=#{1}")    @ResultMap("com.hunter.sys.user.dao.SysUserDao.allResultMap")    SysUser getOneByTypeAndPhone(String type,String Phone);}